const canvas = document.querySelector('canvas')
const ctx = canvas.getContext('2d')
canvas.width = innerWidth
canvas.height = innerHeight

const delta = canvas.width / 2 - 100
const rad = 250
const count = 30
const lineW = 40
const outsideCircle = []
const insideCircle = []

function Line (from, posTo, style) {
  this.posFrom = from
  this.posTo = posTo
  this.style = style
  this.updated = false

  this.update = () => {
    this.draw()
  }

  this.draw = () => {
    ctx.beginPath()
    ctx.moveTo(this.posFrom.x, this.posFrom.y)
    ctx.lineTo(this.posTo.x, this.posTo.y)
    ctx.strokeStyle = this.style.color
    ctx.lineWidth = this.style.width
    ctx.stroke()
    ctx.closePath()
  }
}

for (let i = -3; i <= count + 3; i++) {
  // draw outside (main) circle
  let newX = Math.cos(i * Math.PI / count) * rad
  let newY = Math.sin(i * Math.PI / count) * rad
  const outsideLine = new Line(
    {
      x: newX + delta,
      y: (newY - delta) * -1
    },
    {
      x: newX - 40 * Math.cos(i * Math.PI / count) + delta,
      y: (newY - 40 * Math.sin(i * Math.PI / count) - delta) * -1
    },
    {
      color: '#3E2F5B',
      width: 5
    }
  )
  outsideCircle.push(outsideLine)

  // draw inside circle
  newX = Math.cos(i * Math.PI / count) * (rad - 70)
  newY = Math.sin(i * Math.PI / count) * (rad - 70)
  const insideLine = new Line(
    {
      x: newX + delta,
      y: (newY - delta) * -1
    },
    {
      x: newX - 10 * Math.cos(i * Math.PI / count) + delta,
      y: (newY - 10 * Math.sin(i * Math.PI / count) - delta) * -1
    },
    {
      color: '#7E8287',
      width: 5
    }
  )
  insideCircle.push(insideLine)
}

// initial
ctx.fillStyle = '#261132'
ctx.fillRect(0, 0, canvas.width, canvas.height)
outsideCircle.forEach(el => {
  el.update()
})
insideCircle.forEach(el => {
  el.update()
})

